import React, { useEffect, useRef, useState } from 'react';

const OsciloscopioPortadora = ({ params }) => {
  const containerRef = useRef(null);
  const chartRef = useRef(null);
  const dataSeriesRef = useRef(null);
  const xAxisRef = useRef(null);
  const intervalRef = useRef(null);
  const [isReady, setIsReady] = useState(false);
  const bufferRef = useRef([]); // Buffer para almacenar los datos

  const updateInterval = 10; // Intervalo de actualización en milisegundos (10 ms para mayor suavidad)

  useEffect(() => {
    const checkReadiness = () => {
      if (document.getElementById("osciloscopio-portadora") && window.SciChart) {
        setIsReady(true);
      } else {
        setTimeout(checkReadiness, 100);
      }
    };

    checkReadiness();

    return () => {
      if (chartRef.current) {
        chartRef.current.delete();
        chartRef.current = null;
      }
      if (intervalRef.current) {
        clearInterval(intervalRef.current);
      }
    };
  }, []);

  useEffect(() => {
    if (!isReady) return;

    let isComponentMounted = true;

    const initSciChart = async () => {
      try {
        const {
          SciChartSurface,
          NumericAxis,
          FastLineRenderableSeries,
          XyDataSeries,
          NumberRange
        } = window.SciChart;

        const { sciChartSurface, wasmContext } = await SciChartSurface.create("osciloscopio-portadora");

        if (!isComponentMounted) {
          sciChartSurface.delete();
          return;
        }

        sciChartSurface.background = "#121212";

        // Configurar ejes
        const xAxis = new NumericAxis(wasmContext, {
          axisTitle: "Tiempo (s)",
          labelStyle: { color: "#e0e0e0" },
          titleStyle: { color: "#e0e0e0" },
          majorGridLineStyle: { color: "#333", strokeThickness: 1 },
          tickLabelStyle: { color: "#e0e0e0" },
          visibleRange: new NumberRange(0, 0.1) // Rango fijo de 0.1 segundos
        });

        const yAxis = new NumericAxis(wasmContext, {
          axisTitle: "Voltaje (V)",
          visibleRange: new NumberRange(-6, 6),
          labelStyle: { color: "#e0e0e0" },
          titleStyle: { color: "#e0e0e0" },
          majorGridLineStyle: { color: "#333", strokeThickness: 1 },
          tickLabelStyle: { color: "#e0e0e0" }
        });

        sciChartSurface.xAxes.add(xAxis);
        sciChartSurface.yAxes.add(yAxis);

        const signalData = new XyDataSeries(wasmContext);

        const lineSeries = new FastLineRenderableSeries(wasmContext, {
          stroke: "#FF5722",
          dataSeries: signalData,
          strokeThickness: 1
        });

        sciChartSurface.renderableSeries.add(lineSeries);

        chartRef.current = sciChartSurface;
        dataSeriesRef.current = signalData;
        xAxisRef.current = xAxis;

        console.log("SciChart Portadora inicializado correctamente");
      } catch (error) {
        console.error("Error inicializando SciChart Portadora:", error);
      }
    };

    initSciChart();

    return () => {
      isComponentMounted = false;
    };
  }, [isReady]);

  const generateRealTimeData = () => {
    if (!dataSeriesRef.current || !params) return;

    const currentTime = Date.now() / 1000; // Tiempo actual en segundos
    const angularFreq = 2 * Math.PI * params.frecuencia;
    const y = params.voltaje * Math.sin(angularFreq * (currentTime % (1 / params.frecuencia)) + params.fase);

    // Agregar el nuevo punto de datos al buffer
    bufferRef.current.push({ x: currentTime, y });

    // Eliminar datos antiguos (más de 0.1 segundos)
    const oldestTime = currentTime - 0.1;
    bufferRef.current = bufferRef.current.filter(point => point.x >= oldestTime);

    // Actualizar la serie de datos en SciChart
    dataSeriesRef.current.clear();
    bufferRef.current.forEach(point => {
      dataSeriesRef.current.append(point.x, point.y);
    });

    // Ajustar el rango visible del eje X
    if (xAxisRef.current) {
      xAxisRef.current.visibleRange = new window.SciChart.NumberRange(oldestTime, currentTime);
    }
  };

  useEffect(() => {
    if (!isReady) return;

    // Iniciar el intervalo para actualizar los datos en tiempo real
    intervalRef.current = setInterval(generateRealTimeData, updateInterval);

    // Limpiar el intervalo cuando el componente se desmonte
    return () => {
      if (intervalRef.current) {
        clearInterval(intervalRef.current);
      }
    };
  }, [isReady, params]);

  return (
    <div id="osciloscopio-portadora" ref={containerRef} style={{ width: '100%', height: '350px', backgroundColor: '#121212' }}></div>
  );
};

export default OsciloscopioPortadora;
