import React, { useEffect, useRef, useState } from 'react';

const OsciloscopioPortadora = ({ params }) => {
  const containerRef = useRef(null);
  const chartRef = useRef(null);
  const dataSeriesRef = useRef(null);
  const [isReady, setIsReady] = useState(false);

  // Primer efecto: solo establece isReady cuando el DOM y SciChart están listos
  useEffect(() => {
    // Verificamos si el contenedor con ID específico y SciChart están disponibles
    const checkReadiness = () => {
      const container = document.getElementById("osciloscopio-portadora");
      if (container && window.SciChart) {
        setIsReady(true);
      } else {
        setTimeout(checkReadiness, 100);
      }
    };

    checkReadiness();

    return () => {
      // Limpieza al desmontar
      if (chartRef.current) {
        chartRef.current.delete();
        chartRef.current = null;
      }
    };
  }, []); // Solo se ejecuta una vez al montar

  // Segundo efecto: inicializa SciChart una vez que todo esté listo
  useEffect(() => {
    if (!isReady) return;

    let isComponentMounted = true;

    const initSciChart = async () => {
      try {
        // Acceder a SciChart
        const {
          SciChartSurface,
          NumericAxis,
          FastLineRenderableSeries,
          XyDataSeries,
          NumberRange
        } = window.SciChart;

        // Usamos el ID para inicializar, pero solo después de haber verificado que existe
        const { sciChartSurface, wasmContext } = await SciChartSurface.create("osciloscopio-portadora");

        if (!isComponentMounted) {
          sciChartSurface.delete();
          return;
        }

        sciChartSurface.background = "#121212"; // Fondo oscuro

        // Configurar ejes
        const xAxis = new NumericAxis(wasmContext, { 
          axisTitle: "Tiempo (s)",
          labelStyle: { color: "#e0e0e0" },
          titleStyle: { color: "#e0e0e0" },
          majorGridLineStyle: { color: "#333", strokeThickness: 1 },
          tickLabelStyle: { color: "#e0e0e0" },
          visibleRange: new NumberRange(0, 0.1) // Mostrar 0.1 segundos
        });

        const yAxis = new NumericAxis(wasmContext, { 
          axisTitle: "Voltaje (V)", 
          visibleRange: new NumberRange(-6, 6), // Ajustado para el rango de voltaje
          labelStyle: { color: "#e0e0e0" },
          titleStyle: { color: "#e0e0e0" },
          majorGridLineStyle: { color: "#333", strokeThickness: 1 },
          tickLabelStyle: { color: "#e0e0e0" }
        });

        sciChartSurface.xAxes.add(xAxis);
        sciChartSurface.yAxes.add(yAxis);

        // Series de datos
        const signalData = new XyDataSeries(wasmContext);

        const lineSeries = new FastLineRenderableSeries(wasmContext, { 
          stroke: "#FF5722", // Naranja para la portadora
          dataSeries: signalData,
          strokeThickness: 2.5
        });

        sciChartSurface.renderableSeries.add(lineSeries);

        // Guardar referencias
        chartRef.current = sciChartSurface;
        dataSeriesRef.current = signalData;

        console.log("SciChart Portadora inicializado correctamente");

        // Generar datos iniciales si los parámetros están disponibles
        if (params?.voltaje !== undefined && 
            params?.frecuencia !== undefined && 
            params?.fase !== undefined) {
          generateStaticData();
        }
      } catch (error) {
        console.error("Error inicializando SciChart Portadora:", error);
        console.dir(error);
      }
    };

    initSciChart();

    return () => {
      isComponentMounted = false;
    };
  }, [isReady]); // Se ejecuta cuando isReady cambia a true

  // Tercer efecto: actualiza los datos cuando cambian los parámetros
  useEffect(() => {
    if (dataSeriesRef.current && params?.voltaje !== undefined && 
        params?.frecuencia !== undefined && params?.fase !== undefined) {
      generateStaticData();
    }
  }, [params?.voltaje, params?.frecuencia, params?.fase]);

  const generateStaticData = () => {
    if (!dataSeriesRef.current) return;

    dataSeriesRef.current.clear();

    // Generar datos para un ciclo completo
    const numPoints = 1000;
    const duration = 0.1; // 0.1 segundos

    for (let i = 0; i < numPoints; i++) {
      const time = (i / numPoints) * duration;
      // Convertir frecuencia de Hz a radianes/segundo (2π * f)
      const angularFreq = 2 * Math.PI * params.frecuencia;
      const y = params.voltaje * Math.sin(angularFreq * time + params.fase);

      dataSeriesRef.current.append(time, y);
    }
  };

  return (
    <div id="osciloscopio-portadora" ref={containerRef} style={{ width: '100%', height: '350px', backgroundColor: '#121212' }}></div>
  );
};

export default OsciloscopioPortadora;
