import React, { useEffect, useRef, useState } from 'react';

const OsciloscopioPortadora = ({ params }) => {
  const containerRef = useRef(null);
  const chartRef = useRef(null);
  const dataSeriesRef = useRef(null);
  const xAxisRef = useRef(null);
  const yAxisRef = useRef(null);
  const intervalRef = useRef(null);
  const [isReady, setIsReady] = useState(false);
  const timeOffsetRef = useRef(0);

  useEffect(() => {
    const checkReadiness = () => {
      if (document.getElementById("osciloscopio-portadora") && window.SciChart) {
        setIsReady(true);
      } else {
        setTimeout(checkReadiness, 100);
      }
    };

    checkReadiness();

    return () => {
      if (chartRef.current) {
        chartRef.current.delete();
        chartRef.current = null;
      }
      if (intervalRef.current) {
        clearInterval(intervalRef.current);
      }
    };
  }, []);

  useEffect(() => {
    if (!isReady) return;

    let isComponentMounted = true;

    const initSciChart = async () => {
      try {
        const {
          SciChartSurface,
          NumericAxis,
          FastLineRenderableSeries,
          XyDataSeries,
          NumberRange
        } = window.SciChart;

        const { sciChartSurface, wasmContext } = await SciChartSurface.create("osciloscopio-portadora");

        if (!isComponentMounted) {
          sciChartSurface.delete();
          return;
        }

        sciChartSurface.background = "#121212";

        // Configurar ejes
        const xAxis = new NumericAxis(wasmContext, {
          axisTitle: "Tiempo (s)",
          labelStyle: { color: "#e0e0e0" },
          titleStyle: { color: "#e0e0e0" },
          majorGridLineStyle: { color: "#333", strokeThickness: 1 },
          tickLabelStyle: { color: "#e0e0e0" },
          visibleRange: new NumberRange(0, 0.01)
        });

        const yAxis = new NumericAxis(wasmContext, {
          axisTitle: "Voltaje (V)",
          visibleRange: new NumberRange(-6, 6),
          labelStyle: { color: "#e0e0e0" },
          titleStyle: { color: "#e0e0e0" },
          majorGridLineStyle: { color: "#333", strokeThickness: 1 },
          tickLabelStyle: { color: "#e0e0e0" }
        });

        sciChartSurface.xAxes.add(xAxis);
        sciChartSurface.yAxes.add(yAxis);

        const signalData = new XyDataSeries(wasmContext);

        const lineSeries = new FastLineRenderableSeries(wasmContext, {
          stroke: "#FF5722",
          dataSeries: signalData,
          strokeThickness: 2
        });

        sciChartSurface.renderableSeries.add(lineSeries);

        chartRef.current = sciChartSurface;
        dataSeriesRef.current = signalData;
        xAxisRef.current = xAxis;
        yAxisRef.current = yAxis;

        console.log("SciChart Portadora inicializado correctamente");

        if (params?.voltaje !== undefined &&
          params?.frecuencia !== undefined &&
          params?.fase !== undefined) {
            generateStaticData();
        }
      } catch (error) {
        console.error("Error inicializando SciChart Portadora:", error);
      }
    };

    initSciChart();

    return () => {
      isComponentMounted = false;
    };
  }, [isReady]);

  useEffect(() => {
    if (dataSeriesRef.current && params?.voltaje !== undefined &&
      params?.frecuencia !== undefined && params?.fase !== undefined) {
        generateStaticData();
    }
  }, [params?.voltaje, params?.frecuencia, params?.fase]);

  const generateStaticData = () => {
    if (!dataSeriesRef.current) return;

    dataSeriesRef.current.clear();

    // Mantener el rango de tiempo fijo para todas las frecuencias
    const fixedDuration = 1; // 1 segundo fijo siempre para ver el efecto real de la frecuencia

    // Calcular el número de puntos necesarios para representar adecuadamente la señal
    // Para frecuencias altas necesitamos más puntos para evitar aliasing
    const pointsPerHz = 20; // Puntos por Hz para garantizar calidad
    const numPoints = Math.max(1000, Math.round(params.frecuencia * pointsPerHz * fixedDuration));

    // Generar datos dentro del rango de tiempo fijo
    for (let i = 0; i < numPoints; i++) {
      const time = (i / numPoints) * fixedDuration;
      const angularFreq = 2 * Math.PI * params.frecuencia;
      const y = params.voltaje * Math.sin(angularFreq * time + params.fase);

      dataSeriesRef.current.append(time, y);
    }

    // Ajustar el eje X para mostrar siempre el mismo intervalo de tiempo
    if (xAxisRef.current) {
      xAxisRef.current.visibleRange = new window.SciChart.NumberRange(0, fixedDuration);
    }

    // Ajustar el eje Y para adaptarse a la amplitud de la señal
    if (yAxisRef.current) {
      const yRange = Math.max(6, Math.abs(params.voltaje) * 1.2);
      yAxisRef.current.visibleRange = new window.SciChart.NumberRange(-yRange, yRange);
    }
  };

  return (
    <div id="osciloscopio-portadora" ref={containerRef} style={{ width: '100%', height: '350px', backgroundColor: '#121212' }}>
      {!isReady && (
        <div style={{ color: '#e0e0e0', textAlign: 'center', paddingTop: '150px' }}>
          Cargando osciloscopio...
        </div>
      )}
    </div>
  );
};

export default OsciloscopioPortadora;
