import React, { useEffect, useRef, useState } from 'react';

const OsciloscopioPortadora = ({ params }) => {
  const containerRef = useRef(null);
  const chartRef = useRef(null);
  const dataSeriesRef = useRef(null);
  const xAxisRef = useRef(null);
  const yAxisRef = useRef(null);
  const intervalRef = useRef(null);
  const [isReady, setIsReady] = useState(false);
  const timeOffsetRef = useRef(0);

  useEffect(() => {
    const checkReadiness = () => {
      if (document.getElementById("osciloscopio-portadora") && window.SciChart) {
        setIsReady(true);
      } else {
        setTimeout(checkReadiness, 100);
      }
    };

    checkReadiness();

    return () => {
      if (chartRef.current) {
        chartRef.current.delete();
        chartRef.current = null;
      }
      if (intervalRef.current) {
        clearInterval(intervalRef.current);
      }
    };
  }, []);

  useEffect(() => {
    if (!isReady) return;

    let isComponentMounted = true;

    const initSciChart = async () => {
      try {
        const {
          SciChartSurface,
          NumericAxis,
          FastLineRenderableSeries,
          XyDataSeries,
          NumberRange
        } = window.SciChart;

        const { sciChartSurface, wasmContext } = await SciChartSurface.create("osciloscopio-portadora");

        if (!isComponentMounted) {
          sciChartSurface.delete();
          return;
        }

        sciChartSurface.background = "#121212";

        // Configurar ejes con divisiones más pequeñas
        const xAxis = new NumericAxis(wasmContext, {
          axisTitle: "Tiempo (s)",
          labelStyle: { color: "#e0e0e0" },
          titleStyle: { color: "#e0e0e0" },
          majorGridLineStyle: { color: "#333", strokeThickness: 1 },
          minorGridLineStyle: { color: "#222", strokeThickness: 0.5 },
          tickLabelStyle: { color: "#e0e0e0" },
          visibleRange: new NumberRange(0, 0.1),
          majorDelta: 0.1,
          minorDelta: 0.02,
          drawMajorBands: false,
          drawMinorGridLines: true,
          drawMajorGridLines: true,
          autoRange: false
        });

        const yAxis = new NumericAxis(wasmContext, {
          axisTitle: "Voltaje (V)",
          visibleRange: new NumberRange(-6, 6),
          labelStyle: { color: "#e0e0e0" },
          titleStyle: { color: "#e0e0e0" },
          majorGridLineStyle: { color: "#333", strokeThickness: 1 },
          minorGridLineStyle: { color: "#222", strokeThickness: 0.5 },
          tickLabelStyle: { color: "#e0e0e0" },
          majorDelta: 1,
          minorDelta: 0.2,
          drawMinorGridLines: true,
          autoRange: false
        });

        sciChartSurface.xAxes.add(xAxis);
        sciChartSurface.yAxes.add(yAxis);

        const signalData = new XyDataSeries(wasmContext);

        const lineSeries = new FastLineRenderableSeries(wasmContext, {
          stroke: "#FF5722",
          dataSeries: signalData,
          strokeThickness: 2
        });

        sciChartSurface.renderableSeries.add(lineSeries);

        // Añadir modificadores para zoom y pan
        const zoomPanModifier = new window.SciChart.ZoomPanModifier();
        const mouseWheelZoomModifier = new window.SciChart.MouseWheelZoomModifier();
        sciChartSurface.chartModifiers.add(zoomPanModifier);
        sciChartSurface.chartModifiers.add(mouseWheelZoomModifier);

        chartRef.current = sciChartSurface;
        dataSeriesRef.current = signalData;
        xAxisRef.current = xAxis;
        yAxisRef.current = yAxis;

        console.log("SciChart Portadora inicializado correctamente");

        if (params?.voltaje !== undefined &&
          params?.frecuencia !== undefined &&
          params?.fase !== undefined) {
          startRealTimeUpdates();
        }
      } catch (error) {
        console.error("Error inicializando SciChart Portadora:", error);
      }
    };

    initSciChart();

    return () => {
      isComponentMounted = false;
    };
  }, [isReady]);

  useEffect(() => {
    if (dataSeriesRef.current && params?.voltaje !== undefined &&
      params?.frecuencia !== undefined && params?.fase !== undefined) {
      // Detener el intervalo actual si existe
      if (intervalRef.current) {
        clearInterval(intervalRef.current);
      }
      // Reiniciar el offset de tiempo
      timeOffsetRef.current = 0;
      // Iniciar una nueva actualización en tiempo real con los nuevos parámetros
      startRealTimeUpdates();
    }
  }, [params?.voltaje, params?.frecuencia, params?.fase]);

  const startRealTimeUpdates = () => {
    if (!dataSeriesRef.current || !xAxisRef.current || !yAxisRef.current) return;

    // Ajustar el eje Y para adaptarse a la amplitud de la señal
    if (yAxisRef.current) {
      const yRange = Math.max(6, Math.abs(params.voltaje) * 1.2);
      yAxisRef.current.visibleRange = new window.SciChart.NumberRange(-yRange, yRange);
    }

    // Configurar el intervalo de tiempo visible
    const windowSize = 0.1; // 100ms de ventana visible

    // Ajustar divisiones automáticamente según la frecuencia
    if (xAxisRef.current && params.frecuencia) {
      // Para frecuencias más altas, usamos divisiones más pequeñas
      const cycleTime = 1 / params.frecuencia;

      // Ajustar las divisiones principales para mostrar aproximadamente 1-2 ciclos
      const majorDelta = cycleTime;
      // Divisiones secundarias para mostrar aproximadamente 5 por ciclo
      const minorDelta = cycleTime / 5;

      // Limitar las divisiones a valores razonables
      xAxisRef.current.majorDelta = Math.max(0.01, Math.min(0.1, majorDelta));
      xAxisRef.current.minorDelta = Math.max(0.002, Math.min(0.02, minorDelta));
    }

    // Generar datos iniciales
    const pointsPerWindow = 500; // Cantidad de puntos en la ventana visible

    // Generar más puntos para tener una reserva
    const bufferSize = pointsPerWindow * 2;

    const xValues = [];
    const yValues = [];

    for (let i = 0; i < bufferSize; i++) {
      const time = timeOffsetRef.current + (i / pointsPerWindow) * windowSize;
      const angularFreq = 2 * Math.PI * params.frecuencia;
      const y = params.voltaje * Math.sin(angularFreq * time + params.fase);

      xValues.push(time);
      yValues.push(y);
    }

    // Actualizar la serie de datos
    dataSeriesRef.current.clear();
    dataSeriesRef.current.appendRange(xValues, yValues);

    // Configurar el rango visible inicial
    xAxisRef.current.visibleRange = new window.SciChart.NumberRange(
      timeOffsetRef.current, 
      timeOffsetRef.current + windowSize
    );

    // Iniciar actualizaciones en tiempo real
    const updateInterval = 16; // ~60 FPS (en ms)
    const timeStep = updateInterval / 1000; // Convertir a segundos

    // Iniciar intervalo de actualización
    intervalRef.current = setInterval(() => {
      if (!dataSeriesRef.current || !xAxisRef.current) return;

      // Incrementar el tiempo
      timeOffsetRef.current += timeStep;

      // Actualizar el rango visible
      const currentMin = timeOffsetRef.current;
      const currentMax = currentMin + windowSize;
      xAxisRef.current.visibleRange = new window.SciChart.NumberRange(currentMin, currentMax);

      // Añadir nuevos puntos
      const newXValues = [];
      const newYValues = [];
      const pointsToAdd = Math.max(1, Math.ceil(pointsPerWindow * timeStep / windowSize));

      for (let i = 0; i < pointsToAdd; i++) {
        const time = currentMax + (i / pointsPerWindow) * windowSize;
        const angularFreq = 2 * Math.PI * params.frecuencia;
        const y = params.voltaje * Math.sin(angularFreq * time + params.fase);

        newXValues.push(time);
        newYValues.push(y);
      }

      // Añadir los nuevos puntos
      dataSeriesRef.current.appendRange(newXValues, newYValues);

      // Eliminar puntos antiguos para mantener el rendimiento
      if (dataSeriesRef.current.count() > bufferSize) {
        dataSeriesRef.current.removeRange(0, pointsToAdd);
      }
    }, updateInterval);
  };

  return (
    <div id="osciloscopio-portadora" ref={containerRef} style={{ width: '100%', height: '350px', backgroundColor: '#121212' }}>
      {!isReady && (
        <div style={{ color: '#e0e0e0', textAlign: 'center', paddingTop: '150px' }}>
          Cargando osciloscopio...
        </div>
      )}
    </div>
  );
};

export default OsciloscopioPortadora;
